def	p(g):B=[i	for(i,r)in	enumerate(g)if	all(x==0for	x	in	r)];b,z=B[0],B[-1]+1;n,m=len(g),len(g[0]);C=[j	for	j	in	range(m)if	all(r[j]==0for	r	in	g)];e,E=C[0],C[-1]+1;D=[[[g[i][j]for	j	in	range(C,D)]for	i	in	range(A,B)]for(A,B)in((0,b),(z,n))for(C,D)in((0,e),(E,m))];A=[max(set(F:=[v	for	A	in	A	for	v	in	A	if	v]),key=F.count)for	A	in	D];v=[x	for	x	in	set(A)if	A.count(x)==1][0];return	D[A.index(v)]