def	p(g):A,B=len(g),len(g[0]);C=sorted({0,B}|{i+1for	i	in	range(B-1)if	any(g[y][i]!=g[y][i+1]for	y	in	range(A))});D=sorted({0,A}|{i+1for	i	in	range(A-1)if	any(g[i][x]!=g[i+1][x]for	x	in	range(B))});E=[[g[(D[i]+D[i+1]-1)//2][(C[j]+C[j+1]-1)//2]for	j	in	range(len(C)-1)]for	i	in	range(len(D)-1)];E=[r	for	r	in	E	if	any(r)];F=zip(*E);F=[r	for	r	in	F	if	any(r)];return	list(map(list,zip(*F)))